import { component } from "spak/decorators";
import { SpecRegistration, ActionSpec } from "spak-di";
import DashboardVC from "./lib/ui/DashboardVC";
import LoadDashboard from "./lib/actions/LoadDashboard";

@component("@app/dashboard")
export default class DashboardComponent {
    register() {
        return new SpecRegistration(
            new ActionSpec("loadDashboard", LoadDashboard)
        );
    }
}

export { DashboardVC };
