import { App, Action } from "spak"

@Action.ns("Dashboard")
export default class LoadDashboard extends Action {
    constructor(stores) {
        super(stores);
        this._stores = stores;
    }

    exec({ presenter }) {
        //this._loadStores();
        presenter.showApp();
    }

    _loadStores() {
        App.session().isAuthenticated = true; // storage loadAll checks if authenticated.
        this._stores.loadAll();
    }
}
