import React from "react";
import { App } from "spak";
import { autobind } from "core-decorators";

export default class Dashboard extends React.Component {
    constructor(props) {
        super(props);
    }

    render() {
        return (
            <div className="dashboard">
                <h1>Dashboard:</h1>
                <p>Continue working on your courses, and review your progress.</p>
                <button onClick={this._signOut}>Sign Out</button>
            </div>
        );
    }

    @autobind
    _signOut() {
        App.dispatchAction("signOut", { presenter: this });
    }

    showSignInScreen() {
        window.location.hash = "/";
    }
};
