import { component } from "spak/decorators";
import { SpecRegistration, ActionSpec } from "spak-di";
import SignInVC from "./lib/ui/SignInVC";
import SignIn from "./lib/actions/SignIn";
import SignOut from "./lib/actions/SignOut";

@component("@app/sign-in")
export default class SignInComponent {
    register() {
        return new SpecRegistration(
            new ActionSpec(SignIn),
            new ActionSpec(SignOut)
        );
    }
}

export { SignInVC };
